#define PI 3.1415926535897932384626433832795

#define cx_mul(a, b) vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x)
#define cx_div(a, b) vec2(((a.x*b.x + a.y*b.y)/(b.x*b.x + b.y*b.y)),((a.y*b.x - a.x*b.y)/(b.x*b.x + b.y*b.y)))
#define cx_sin(a) vec2(sin(a.x) * cosh(a.y), cos(a.x) * sinh(a.y))
#define cx_cos(a) vec2(cos(a.x) * cosh(a.y), -sin(a.x) * sinh(a.y))

vec2 cx_tan(vec2 a) {return cx_div(cx_sin(a), cx_cos(a)); }
vec2 cx_log(vec2 a) {
    float rpart = sqrt((a.x*a.x)+(a.y*a.y));
    float ipart = atan(a.y,a.x);
    if (ipart > PI) ipart=ipart-(2.0*PI);
    return vec2(log(rpart),ipart);
}

vec2 as_polar(vec2 z) {
  return vec2(length(z), atan(z.y, z.x));
}
vec2 cx_pow(vec2 v, float p) {
  vec2 z = as_polar(v);
  return pow(z.x, p) * vec2(cos(z.y * p), sin(z.y * p));
}

float im(vec2 z) {
  return ((atan(z.y, z.x) / PI) + 1.0) * 0.5;
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos(2.*PI*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {  
    float t = iTime * 0.1;
    // Define our points
    vec2 p[8];
    for(int i=0; i<8; i++) {
      p[i] = vec2(cos(float(i) * 0.1 + t), sin(float(i) * 2.3 + t));
    }

    // Set up our imaginary plane
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);
    vec2 z = uv * 4.;

    // Calculate the sum of our first polynomial
    vec2 polyA = p[0]
        + cx_mul(p[1], z)
        + cx_mul(p[2], cx_pow(z, 2.0))
        + cx_mul(p[3], cx_pow(z, 3.0));

    // Calculate the sum of our second polynomial
    vec2 polyB = p[4]
        + cx_mul(p[5], z)
        + cx_mul(p[6], cx_pow(z, 2.0))
        + cx_mul(p[7], cx_pow(z, 3.0));

    // Calculate the ratio of our complex polynomials
    vec2 result = cx_div(polyA, polyB);

    float imaginary = cx_log(result).y;
    float col = (imaginary / PI);
    fragColor = vec4(pal(col, vec3(.52,.45,.21),vec3(.40,.40,.31),vec3(.26,.30,.75),vec3(.15,.4,.2)), 1.0);
}
